import React, { Component } from 'react';
import './App.css';

const emailValidacion = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);
const formValid = ({ errores, ...rest }) => {
  let valid = true;
  Object.values(errores).forEach(val => { val.length > 0 && (valid = false); });
  Object.values(rest).forEach(val => { val === null && (valid = false); });
  return valid;
};

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: null,
      password: null,
      errores: {
        email: "",
        password: ""
      }
    };
  }

  handleSubmit = e => {
    e.preventDefault();

    if (formValid(this.state)) {
      //formulario válido
    } else {
      //Error en el formulario
      console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
    }
  };

  validar = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let errores = { ...this.state.errores };

    switch (name) {
      case "email":
        errores.email = emailValidacion.test(value)
          ? ""
          : "El email ingresado es inválido";
        break;
      case "password":
        errores.password =
          value.length < 4 ? "La contraseña es muy corta" : "";
        break;
      default:
        break;
    }

    this.setState({ errores, [name]: value }, () => console.log(this.state));
  };

  render() {
    const { errores, ingresarDisabled = true } = this.state;
    return (
      <div className='contenedor'>
        <div className="imagen-estado">

          <img src="images/inicial.svg" width="100%" />
        </div>
        <div className="formulario">
          <form noValidate>
          <h2>Iniciar Sesión</h2>
            <div className="campo campo-mail">
              <img src="images/mail.svg" width="25" className="icono" />
              <input 
                type="email" 
                name="email" 
                className={errores.email.length > 0 ? "error" : null}
                placeholder="Email" 
                onChange={this.validar}
                noValidate
              />
              {errores.email.length > 0 && (
                <span className="errorMessage">
                  <img src="images/cross.svg" width="18" /> {errores.email}
                </span>
              )}
            </div>
            <div className="campo">
            <img src="images/password.svg" width="25" className="icono" />
              <input 
                type="password" 
                name="password" 
                className={errores.password.trim() == "" ? "error" : null}
                placeholder="Contraseña" 
                onChange={this.validar}
                noValidate
              />
              {errores.password.length > 0 && (
                <span className="errorMessage">{errores.password}</span>
              )}
            </div>
            <div className="campo">
              <button disabled={ingresarDisabled}>Ingresar</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default App;
