import React, { Component } from 'react';
import './App.css';

const emailValidacion = RegExp(
  /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

class App extends Component {
  state = this.getInitialState();

  getInitialState() {
    return {
      mail: '',
      password: '',
      ingresarDisabled: true
    };
  }

  render() {
    const { mail, password, ingresarDisabled, formErrores } = this.state;

    return (
      <div className='contenedor'>
        <div className="imagen-estado">
          {formErrores.mail.length > 0 &&(
            <span></span>
          )}
          <img src="images/inicial.svg" width="100%" />
        </div>
        <div className="formulario">
          <form noValidate>
          <h2>Iniciar Sesión</h2>
            <div className="campo campo-mail">
              <img src="images/mail.svg" width="25" className="icono" />
              <input type="mail" name="mail" placeholder="Email" onChange="{this.validar}"  />
            </div>
            <div className="campo">
            <img src="images/password.svg" width="25" className="icono" />
              <input type="password" name="password" placeholder="Contraseña" onChange="{this.validar}" />
            </div>
            <div className="campo">
              <button disabled={ingresarDisabled}>Ingresar</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default App;
