          {formErrores.mail.length > 0 &&(
            <span></span>
          )}
          <img src="images/inicial.svg" width="100%" />
        </div>
        <div className="formulario">
          <form noValidate>
          <h2>Iniciar Sesión</h2>
            <div className="campo campo-mail">
              <img src="images/mail.svg" width="25" className="icono" />
              <input type="mail" name="mail" placeholder="Email" onChange="{this.validar}"  />
            </div>
            <div className="campo">
            <img src="images/password.svg" width="25" className="icono" />
              <input type="password" name="password" placeholder="Contraseña" onChange="{this.validar}" />
            </div>
            <div className="campo">
              <button disabled={ingresarDisabled}>Ingresar</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default App;
