import React, { Component } from 'react';
import './App.css';

const emailValidacion = RegExp(
  /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
);
const formValid = ({ errores, ...rest }) => {
  let valid = true;
  Object.values(errores).forEach(val => { 
    val.length > 0 && (valid = false); 
  });
  Object.values(rest).forEach(val => { 
    val === null && (valid = false); 
  });
  return valid;
};
const isEnabled = email.length > 0 && password.length > 0;

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      ingresarBtn: true,
      errores: {
        email: "",
        password: ""
      }
    };
  }

  handleSubmit = e => {
    e.preventDefault();
    if (formValid(this.state)) {
      //formulario válido
    } else {
      //Error en el formulario
      console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
    }
  };

  validar = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let errores = { ...this.state.errores };

    switch (name) {
      case "email":
      errores.email = emailValidacion.test(value) ? "" : "El email ingresado es inválido";
        break;
      case "password":
        errores.password = value.trim().length < 4 ? "La contraseña es inválida" : "";
        break;
      default:
        break;
    }

    this.setState({ errores, [name]: value }, () => console.log(this.state));
  };

  render() {
    const { errores } = this.state;

    return (
      <div className='contenedor'>
        <div className="imagen-estado">
        {errores.email.length > 0 || errores.password.length > 0 ? (
          <img src="images/error.svg" width="100%" alt="Errores" />
        ) : (
          <img src="images/inicial.svg" width="100%" alt="Todo bien" />
        )}
        </div>
        <div className="formulario">
          <form noValidate>
          <h2>Iniciar Sesión</h2>
            <div className="campo campo-mail">
              <img src="images/mail.svg" width="25" className="icono" alt="Email" />
              <input 
                type="email" 
                name="email" 
                className={errores.email.length > 0 ? "error" : null}
                placeholder="Email" 
                onChange={this.validar}
                noValidate
              />
              {errores.email.length > 0 && (
                <div className="errorMessage">
                  <img src="images/cross.svg" width="18" alt="Email inválido" /> 
                  <span>{errores.email}</span>
                </div>
              )}
            </div>
            <div className="campo">
            <img src="images/password.svg" width="25" className="icono" alt="Password" />
              <input 
                type="password" 
                name="password" 
                className={errores.password.length > 0 ? "error" : "right"}
                placeholder="Contraseña" 
                onChange={this.validar}
                noValidate
              />
              {errores.password.length > 0 && (
                <div className="errorMessage">
                  <img src="images/cross.svg" width="18" alt="Contraseña inválida" /> 
                  <span>{errores.password}</span>
                </div>
              )}
            </div>
            <div className="campo">
              <button name="ingresar" disabled={!isEnabled}>Ingresar</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default App;
